import { Switch, Route, Link, BrowserRouter } from "react-router-dom";
import Profile from '../Home/profile';
import Posts from '../Home/Posts.js';
import NewRequest from '../Home/NewRequest';
import Upload from '../Home/uploadimg';
import Button from '@material-ui/core/Button';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import {auth} from "../../firebase";
import {fdb} from "../../firebase";


const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));
class SideBar extends React.Component {
  constructor(props){
    super(props)
    this.state ={
      Email:"",
      Password:"",
      Nickname:"",
      auth: this.auth,
      fdb: this.fdb,
      name:"",




    }
    this.handleDrawerToggle = this.handleDrawerToggle.bind(this)
    this.Logout= this.Logout.bind(this)
  }
  // handleDrawerToggle = () => {
  //   const value = this.state.
  //   this.setState({
  //     mobileOpen:
  //   })
  //   setMobileOpen(!this.mobileOpen);
  // }

  Logout=()=>{
    auth.signOut().then(function() {
  // log-out successful.
      console.log(auth.currentUser)
      const ref = fdb.collection('users').doc(this.props.location.state.Email);
      ref.update({
        LoginState: false
      }).then(() => {
        console.log('logout successful');
        this.props.history.push("/login");
      });
    }).catch(err => {
      // An error happened.
      console.log('Error logging out', err);
    });
  }
  render(){
    //const { container,location } = props;
    const classes = useStyles();
    const theme = useTheme;
    return(
      <div>
        <div className={classes.toolbar} />
        <Grid container justify = "center">

          <label htmlFor="icon-button-file">
            <IconButton color="primary" aria-label="upload picture" component="span">
            </IconButton>
          </label>


          <Upload/>
        </Grid>
        <br />
        <br />
        <Divider />
        <List>
          {['Posts', 'New Request', 'Active Transaction', 'Archived'].map((text, index) => (
            <ListItem button key={text} component={Link} to={"/" + text.replace(/\s/g,'')}>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>
        <Divider />
        <List>
          {['Profile', 'Log Out'].map((text, index) => (
            <ListItem button onClick={this.Logout} key={text} component={Link} to={"/" + text.replace(/\s/g,'')}>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>
      </div>
    );

    return (
      <div className={classes.root}>
        <CssBaseline />
        <BrowserRouter>
          <nav className={classes.drawer} aria-label="mailbox folders">
            // {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
            <Hidden xsDown implementation="css">
              <Drawer
                classes={{
                  paper: classes.drawerPaper,
                }}
                variant="permanent"
                open
              >
                {drawer}
              </Drawer>
            </Hidden>
          </nav>
          // {/*--- Add remaing components to each Route  ---*/}
          <main className={classes.content}>
            <div className={classes.toolbar} />
            <Switch>
              <Route exact path="/posts">
                <Posts />
              </Route>
              <Route path="/profile">
                <Profile />
              </Route>
              <Route path="/NewRequest">
                <NewRequest />
              </Route>
            </Switch>
          </main>
        </BrowserRouter>
      </div>
    );
  }


}
