import { withStyles, useTheme } from '@material-ui/core/styles';
import {auth} from "../../firebase";
import {fdb} from "../../firebase";
import { useLocation } from "react-router-dom";


const drawerWidth = 240;

const styles = theme => ({
  root: {
    display: 'flex',
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
