  // }

  Logout=event=>{
    //const loc = useLocation();
    console.log(this.props.location)
    // console.log(prevProps.Email)
    auth.signOut().then(function() {
  // log-out successful.

      const ref = fdb.collection('users').doc(this.props.location.state.Email);
      ref.update({
        LoginState: false
      }).then(() => {
        console.log('logout successful');
        this.props.history.push("/login");
      });
    }).catch(err => {
      // An error happened.
      console.log('Error logging out', err);
    });
  }
  render(){
    const {classes} = this.props;
    const theme = useTheme;
    return(

        <div className={classes.root}>
        <CssBaseline />
        <BrowserRouter>
          <nav className={classes.drawer} aria-label="mailbox folders">
            <Hidden xsDown implementation="css">
              <Drawer
                classes={{
                  paper: styles.drawerPaper,
                }}
                variant="permanent"
                open
              >
              <div>
                <div className={classes.toolbar} />
                <Grid container justify = "center">

                  <label htmlFor="icon-button-file">
                    <IconButton color="primary" aria-label="upload picture" component="span">
                    </IconButton>
                  </label>

                  <Upload/>
                </Grid>
                <br />
                <br />
                <Divider />
                <List>
                  {['Posts', 'New Request', 'Active Transaction', 'Archived'].map((text, index) => (
                    <ListItem button key={text} component={Link} to={"/" + text.replace(/\s/g,'')}>
                      <ListItemIcon>
                        {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                      </ListItemIcon>
                      <ListItemText primary={text} />
                    </ListItem>
                  ))}
                </List>
                <Divider />
                <List>
                  {['Profile', 'Log Out'].map((text, index) => (
                    <ListItem button onClick={this.Logout} key={text} component={Link} to={"/" + text.replace(/\s/g,'')}>
                      <ListItemIcon>
                        {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                      </ListItemIcon>
                      <ListItemText primary={text} />
                    </ListItem>
                  ))}
                </List>
                </div>
              </Drawer>
            </Hidden>
          </nav>
          <main className={classes.content}>
            <div className={classes.toolbar} />
            <Switch>
              <Route exact path="/posts">
                <Posts />
              </Route>
              <Route path="/profile">
                <Profile />
              </Route>
              <Route path="/NewRequest">
                <NewRequest />
              </Route>
            </Switch>
          </main>
        </BrowserRouter>
      </div>
    );
  }


}
